/**
 * Inquirer.js
 * A collection of common interactive command line user interfaces.
 */

import { default as List } from 'inquirer/lib/prompts/list';
import { default as Input } from 'inquirer/lib/prompts/input';
import { default as Number } from 'inquirer/lib/prompts/number';
import { default as Confirm } from 'inquirer/lib/prompts/confirm';
import { default as RawList } from 'inquirer/lib/prompts/rawlist';
import { default as Expand } from 'inquirer/lib/prompts/expand';
import { default as Checkbox } from 'inquirer/lib/prompts/checkbox';
import { default as Password } from 'inquirer/lib/prompts/password';
import { default as Editor } from 'inquirer/lib/prompts/editor';

import { default as BottomBar } from 'inquirer/lib/ui/bottom-bar';
import { default as Prompt } from 'inquirer/lib/ui/prompt';

import { default as Separator } from 'inquirer/lib/objects/separator';

/**
 * Create a new self-contained prompt module.
 */
export function createPromptModule(opt) {
  const promptModule = function (questions, answers) {
    let uiInstance;
    try {
      uiInstance = new Prompt(promptModule.prompts, opt);
    } catch (error) {
      return Promise.reject(error);
    }
    const promise = uiInstance.run(questions, answers);

    // Monkey patch the UI on the promise object so
    // that it remains publicly accessible.
    promise.ui = uiInstance;

    return promise;
  };

  promptModule.prompts = {};

  /**
   * Register a prompt type
   * @param {String} name     Prompt type name
   * @param {Function} prompt Prompt constructor
   * @return {inquirer}
   */

  promptModule.registerPrompt = function (name, prompt) {
    promptModule.prompts[name] = prompt;
    return this;
  };

  /**
   * Register the defaults provider prompts
   */

  promptModule.restoreDefaultPrompts = function () {
    this.registerPrompt('list', List);
    this.registerPrompt('input', Input);
    this.registerPrompt('number', Number);
    this.registerPrompt('confirm', Confirm);
    this.registerPrompt('rawlist', RawList);
    this.registerPrompt('expand', Expand);
    this.registerPrompt('checkbox', Checkbox);
    this.registerPrompt('password', Password);
    this.registerPrompt('editor', Editor);
  };

  promptModule.restoreDefaultPrompts();

  return promptModule;
}

/**
 * Public CLI helper interface
 * @param  {Array|Object|Rx.Observable} questions - Questions settings array
 * @param  {Function} cb - Callback being passed the user answers
 * @return {ui.Prompt}
 */

const prompt = createPromptModule();

// Expose helper functions on the top level for easiest usage by common users
function registerPrompt(name, newPrompt) {
  prompt.registerPrompt(name, newPrompt);
}

function restoreDefaultPrompts() {
  prompt.restoreDefaultPrompts();
}

const inquirer = {
  prompt,
  ui: {
    BottomBar,
    Prompt,
  },
  createPromptModule,
  registerPrompt,
  restoreDefaultPrompts,
  Separator,
};

export default inquirer;
